//Using redux-toolkit
import { createSlice , useConfigureStore } from '@reduxjs/toolkit';

const initialState = { counter:0, showToggle:true}

const counterSlice = createSlice({

    name : 'counter',
    initialState,
    reducers:{

        increment(state){
            state.counter++;
        },
        decrement(state){
            state.counter--;
        },
        increase(state,action){
            state.counter = state.counter + action.amount
        },
        toogleCounter(state){
            state.showToggle =  !state.showToggle
        }
    }
});

//for actions
export const counterActions = counterSlice.actions;

export const store = useConfigureStore(counterSlice.reducer);


























