import { configureStore, createSlice } from "@reduxjs/toolkit";


const initialStateCart= {
    products:[],
    total:0
}

const cartReducer = createSlice({
    name:'cart',
    initialState:initialStateCart,
    reducers :{
        increment(state, action){

            const existingProductIndex = state.products.findIndex(item => item.title === action.payload.title);

            if (existingProductIndex !== -1) {

                const updatedProducts = [...state.products];
                updatedProducts[existingProductIndex].quantity += action.payload.quantity;
                state.products = updatedProducts;
            } else {

                state.products = [...state.products, action.payload];
            }


            state.total += action.payload.price * action.payload.quantity;

            console.log(state.products);
            console.log(state.total);
        },
        decrement(state,action){
            state.products = state.products.filter(product => product.name != action.payload.name);
        }
    }
});


export const cartActions = cartReducer.actions;

const store = configureStore({ 
    reducer :{cart : cartReducer.reducer}
});
export default store;
