import Card from '../UI/Card';
import classes from './ProductItem.module.css';
import { useDispatch, useSelector } from 'react-redux';
import { cartActions } from '../../store/cart.js'
import { UseSelector } from 'react-redux';

const ProductItem = (props) => {
  const { title, price, description } = props;

  const dispatch = useDispatch();
  const cart = useSelector((state) => state.cart);


  function addToCart(title){

    const existingState = cart.products;

    const isExisting = existingState.find((item) => item.title === title)

    dispatch(cartActions.increment({
      title,
      price,
      description,
      quantity: 1,
      total:price
    }));
  }

  return (
    <li className={classes.item}>
      <Card>
        <header>
          <h3>{title}</h3>
          <div className={classes.price}>${price.toFixed(2)}</div>
        </header>
        <p>{description}</p>
        <div className={classes.actions}>
          <button onClick={addToCart(title)}>Add to Cart</button>
        </div>
      </Card>
    </li>
  );
};

export default ProductItem;
